&lt;span class=&#34;hljs-number&#34;&gt;1&lt;/span&gt;;&lt;br&gt;			IF I &lt;span class=&#34;hljs-operator&#34;&gt;&amp;gt;&lt;/span&gt; &lt;span class=&#34;hljs-number&#34;&gt;30&lt;/span&gt; &lt;span class=&#34;hljs-keyword&#34;&gt;THEN&lt;/span&gt; LEAVE tag1; &lt;span class=&#34;hljs-keyword&#34;&gt;END&lt;/span&gt; IF; # 配合标签跳出循环&lt;br&gt;			&lt;span class=&#34;hljs-keyword&#34;&gt;INSERT INTO&lt;/span&gt; admin(`user_name`, `password`)&lt;br&gt;			&lt;span class=&#34;hljs-keyword&#34;&gt;VALUES&lt;/span&gt;(CONCAT(&lt;span class=&#34;hljs-string&#34;&gt;&amp;#x27;JACK&amp;#x27;&lt;/span&gt;, i), &lt;span class=&#34;hljs-string&#34;&gt;&amp;#x27;777&amp;#x27;&lt;/span&gt;);&lt;br&gt;		  &lt;span class=&#34;hljs-keyword&#34;&gt;END&lt;/span&gt; WHILE tag1;&lt;br&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;END&lt;/span&gt; $&lt;br&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
