    &lt;span class=&#34;hljs-keyword&#34;&gt;public&lt;/span&gt; &lt;span class=&#34;hljs-keyword&#34;&gt;void&lt;/span&gt; &lt;span class=&#34;hljs-title function_&#34;&gt;test1&lt;/span&gt;&lt;span class=&#34;hljs-params&#34;&gt;()&lt;/span&gt;&amp;#123;&lt;br&gt;        &lt;span class=&#34;hljs-comment&#34;&gt;//实例化SimpleDateFormat：使用默认构造器&lt;/span&gt;&lt;br&gt;        &lt;span class=&#34;hljs-type&#34;&gt;SimpleDateFormat&lt;/span&gt; &lt;span class=&#34;hljs-variable&#34;&gt;sdf&lt;/span&gt; &lt;span class=&#34;hljs-operator&#34;&gt;=&lt;/span&gt; &lt;span class=&#34;hljs-keyword&#34;&gt;new&lt;/span&gt; &lt;span class=&#34;hljs-title class_&#34;&gt;SimpleDateFormat&lt;/span&gt;();&lt;br&gt;        &lt;span class=&#34;hljs-comment&#34;&gt;//格式化：日期 --&amp;gt; 字符串&lt;/span&gt;&lt;br&gt;        &lt;span class=&#34;hljs-type&#34;&gt;Date&lt;/span&gt; &lt;span class=&#34;hljs-variable&#34;&gt;date&lt;/span&gt; &lt;span class=&#34;hljs-operator&#34;&gt;=&lt;/span&gt; &lt;span class=&#34;hljs-keyword&#34;&gt;new&lt;/span&gt; &lt;span class=&#34;hljs-title class_&#34;&gt;Date&lt;/span&gt;();&lt;br&gt;        System.out.println(date);&lt;span class=&#34;hljs-comment&#34;&gt;//Fri Sep 10 17:42:39 CST 2021&lt;/span&gt;&lt;br&gt;        &lt;span class=&#34;hljs-type&#34;&gt;String&lt;/span&gt; &lt;span class=&#34;hljs-variable&#34;&gt;format&lt;/span&gt; &lt;span class=&#34;hljs-operator&#34;&gt;=&lt;/span&gt; sdf.format(date);&lt;span class=&#34;hljs-comment&#34;&gt;//格式化返回的是字符串&lt;/span&gt;&lt;br&gt;        System.out.println(format);&lt;span class=&#34;hljs-comment&#34;&gt;//21-9-10 下午5:42&lt;/span&gt;&lt;br&gt;        &lt;span class=&#34;hljs-comment&#34;&gt;//解析：字符串 --&amp;gt; 日期&lt;/span&gt;&lt;br&gt;        &lt;span class=&#34;hljs-type&#34;&gt;String&lt;/span&gt; &lt;span class=&#34;hljs-variable&#34;&gt;str&lt;/span&gt; &lt;span class=&#34;hljs-operator&#34;&gt;=&lt;/span&gt; &lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;21-9-12 上午1:00&amp;quot;&lt;/span&gt;;&